children.onTabSelected:
{{(()=>{
	if (heli_tab.selectedTab == "账号") {
		const r = change_page.cur_page();
	get_acount_api.run(()=>{}, ()=>{}, {page: r[0]});
	} else if (heli_tab.selectedTab == "中奖") {
		get_perservation.run();
	}
})()}}

