export default {
	next_data: (day) => {
		if (day == undefined) {
			day = 0
		}
		var dateTime=new Date();
    dateTime=dateTime.setDate(dateTime.getDate()+day);
		var date = new Date(dateTime)
		var Y = date.getFullYear() + '-';
		var M = (date.getMonth()+1 < 10 ? '0'+(date.getMonth()+1) : date.getMonth()+1) + '-';
		var D = (date.getDate() < 10 ? '0'+date.getDate() : date.getDate()) + ' ';
		var h = (date.getHours() < 10 ? '0'+date.getHours() : date.getHours()) + ':';
		var m = (date.getMinutes() < 10 ? '0'+date.getMinutes() : date.getMinutes()) + ':';
		var s = (date.getSeconds() < 10 ? '0'+date.getSeconds() : date.getSeconds());
		return Y+M+D+h+m+s
	},
	// 刷新页面
	refresh_table_func: (pos) => {
		// 计算总量total
		get_account_count_api.run()
		var ret = get_account_count_api.data
		if (ret.code != 0) {
			showAlert(ret.message)
			return [1, 1]
		}
		const total_text = parseInt(ret.data / 10) + 1
		storeValue('total_page_text', total_text)
	  resetWidget("total_page_text")

		// 计算当前页
		var cur_page_text;
		if (pos == undefined || appsmith.store.cur_page_text == undefined) {
			 cur_page_text = 1;
		} else {
			cur_page_text = appsmith.store.cur_page_text + pos;
			if (cur_page_text < 1) {
				cur_page_text = 1;
			} else if (cur_page_text >= total_text) {
				cur_page_text = total_text
			}
		}
		storeValue("cur_page_text", cur_page_text)
		resetWidget("cur_page_text")
		// 获取数据
		return [cur_page_text, total_text]
	}
}
